const workforceData = $fromAI('workforceData', 'Current workforce data including headcount, capacity utilization, and growth targets', 'json');

try {
  const data = typeof workforceData === 'string' ? JSON.parse(workforceData) : workforceData;

  const currentCapacity = parseFloat(data.capacityUtilization || 85);
  const headcount = parseInt(data.headcount || 100);
  const growthRate = parseFloat(data.growthRate || 0.15);
  const attritionRate = parseFloat(data.attritionRate || 0.10);

  // Calculate capacity needs
  const projectedGrowthHeadcount = Math.ceil(headcount * growthRate);
  const projectedAttritionLoss = Math.ceil(headcount * attritionRate);
  const totalCapacityNeed = projectedGrowthHeadcount + projectedAttritionLoss;

  // Calculate capacity gap
  const capacityGap = currentCapacity < 90 ? Math.ceil((90 - currentCapacity) / 100 * headcount) : 0;

  const forecast = {
    currentHeadcount: headcount,
    currentCapacityUtilization: currentCapacity + '%',
    projectedGrowthNeeds: projectedGrowthHeadcount,
    projectedAttritionLoss: projectedAttritionLoss,
    capacityGap: capacityGap,
    totalHiringNeed: totalCapacityNeed + capacityGap,
    recommendation: totalCapacityNeed + capacityGap > 15 ? 'URGENT: Significant hiring required' : 'MODERATE: Planned hiring sufficient',
    forecastDate: new Date().toISOString()
  };

  return JSON.stringify(forecast, null, 2);

} catch (error) {
  return JSON.stringify({
    error: 'Capacity forecast failed',
    message: error.message
  });
}

const employeeData = $fromAI('employeeData', 'Employee data including tenure, performance scores, and department attrition rates', 'json');

try {
  const data = typeof employeeData === 'string' ? JSON.parse(employeeData) : employeeData;

  const avgTenure = parseFloat(data.avgTenure || 3.5);
  const performanceScore = parseFloat(data.performanceScore || 75);
  const attritionRate = parseFloat(data.attritionRate || 10);
  const department = data.department || 'Unknown';

  // Calculate risk factors
  let riskScore = 0;

  // Tenure risk (higher risk for very low or very high tenure)
  if (avgTenure < 1) riskScore += 30;
  else if (avgTenure < 2) riskScore += 20;
  else if (avgTenure > 7) riskScore += 15;

  // Performance risk (low performers more likely to leave or be managed out)
  if (performanceScore < 60) riskScore += 25;
  else if (performanceScore < 70) riskScore += 15;

  // Department attrition trend
  if (attritionRate > 15) riskScore += 30;
  else if (attritionRate > 10) riskScore += 20;
  else if (attritionRate > 5) riskScore += 10;

  // Determine risk level
  let riskLevel = 'LOW';
  if (riskScore >= 50) riskLevel = 'CRITICAL';
  else if (riskScore >= 35) riskLevel = 'HIGH';
  else if (riskScore >= 20) riskLevel = 'MEDIUM';

  const riskAnalysis = {
    department: department,
    riskScore: riskScore,
    riskLevel: riskLevel,
    avgTenure: avgTenure,
    performanceScore: performanceScore,
    departmentAttritionRate: attritionRate + '%',
    recommendation: riskLevel === 'CRITICAL' || riskLevel === 'HIGH' 
      ? 'Immediate retention initiatives required' 
      : 'Monitor and maintain current engagement programs',
    estimatedAttritionImpact: riskLevel === 'CRITICAL' ? 'High probability of significant turnover in next 6 months' : 'Manageable attrition expected',
    calculationDate: new Date().toISOString()
  };

  return JSON.stringify(riskAnalysis, null, 2);

} catch (error) {
  return JSON.stringify({
    error: 'Attrition risk calculation failed',
    message: error.message
  });
}
