parameters.inputSchema:
{
	"type": "object",
	"properties": {
		"credentialId": {
			"type": "string",
			"description": "Unique identifier for the credential being verified"
		},
		"verificationStatus": {
			"type": "string",
			"enum": ["verified", "failed", "pending_review"],
			"description": "Overall verification status of the credential"
		},
		"verificationScore": {
			"type": "number",
			"minimum": 0,
			"maximum": 100,
			"description": "Numerical score representing verification confidence (0-100)"
		},
		"crossReferenceResults": {
			"type": "object",
			"properties": {
				"databaseMatch": {
					"type": "boolean",
					"description": "Whether credential matches database records"
				},
				"authorityValidation": {
					"type": "boolean",
					"description": "Whether credential is validated by issuing authority"
				}
			},
			"required": ["databaseMatch", "authorityValidation"],
			"description": "Results from cross-referencing with external sources"
		},
		"biometricVerification": {
			"type": "boolean",
			"description": "Whether biometric verification passed"
		},
		"documentAuthenticity": {
			"type": "string",
			"enum": ["authentic", "suspicious", "counterfeit"],
			"description": "Assessment of document authenticity"
		},
		"anomaliesDetected": {
			"type": "array",
			"items": {
				"type": "string"
			},
			"description": "List of anomalies or issues detected during verification"
		},
		"recommendedAction": {
			"type": "string",
			"description": "Recommended action based on verification results"
		},
		"reasoning": {
			"type": "string",
			"description": "Detailed reasoning for the verification decision"
		}
	},
	"required": ["credentialId", "verificationStatus", "verificationScore", "crossReferenceResults", "biometricVerification", "documentAuthenticity", "anomaliesDetected", "recommendedAction", "reasoning"]
}
