{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "InfluencerOutreach",
  "type": "object",
  "required": ["outreach"],
  "properties": {
    "outreach": {
      "type": "object",
      "required": ["subject", "message", "priority", "program", "talking_points"],
      "properties": {
        "subject": {
          "type": "string",
          "description": "Personalized email subject line"
        },
        "message": {
          "type": "string",
          "description": "Full personalized email body"
        },
        "priority": {
          "type": "string",
          "enum": ["normal", "medium", "high"],
          "description": "Outreach priority level"
        },
        "program": {
          "type": "string",
          "enum": ["community", "content_collaboration", "strategic_partnership"],
          "description": "Recommended program type"
        },
        "talking_points": {
          "type": "array",
          "items": {"type": "string"},
          "description": "Key personalization elements used"
        }
      }
    }
  }
}

parameters.jsCode:
// FIXED: Multi-platform influencer classification
const items = $input.all();
const results = [];

for (const item of items) {
  const result = item.json.result || {};

  // Multi-platform follower aggregation (prioritize by engagement)
  let platform = null;
  let followers = 0;
  let engagement_rate = 0;
  let username = '';
  let bio = '';

  // Check platforms in order of preference
  if (result.tiktok && result.tiktok.follower_count) {
    platform = 'tiktok';
    followers = result.tiktok.follower_count;
    engagement_rate = result.tiktok.engagement_percent || 0;
    username = result.tiktok.username || '';
    bio = (result.tiktok.biography || '').toLowerCase();
  } else if (result.instagram && result.instagram.follower_count) {
    platform = 'instagram';
    followers = result.instagram.follower_count;
    engagement_rate = result.instagram.engagement_percent || 0;
    username = result.instagram.username || '';
    bio = (result.instagram.biography || '').toLowerCase();
  } else if (result.youtube && result.youtube.subscriber_count) {
    platform = 'youtube';
    followers = result.youtube.subscriber_count;
    engagement_rate = result.youtube.engagement_percent || 0;
    username = result.youtube.username || '';
    bio = (result.youtube.description || '').toLowerCase();
  } else if (result.twitter && result.twitter.follower_count) {
    platform = 'twitter';
    followers = result.twitter.follower_count;
    engagement_rate = result.twitter.engagement_percent || 0;
    username = result.twitter.username || '';
    bio = (result.twitter.biography || '').toLowerCase();
  }

  // Fallback: use first_name if no platform username
  if (!username && result.first_name) {
    username = result.first_name.replace('@', '');
  }

  // Determine tier based on follower count
  let tier = 'nano';
  let tier_name = 'Nano';

  if (followers >= 250000) {
    tier = 'macro';
    tier_name = 'Macro';
  } else if (followers >= 50000) {
    tier = 'mid';
    tier_name = 'Mid-Tier';
  } else if (followers >= 5000) {
    tier = 'micro';
    tier_name = 'Micro';
  }

  // IMPROVED: Niche detection with better keyword matching
  let niche = 'general';
  let niche_name = 'General Creator';

  // Combine bio with other signals
  const content = bio.toLowerCase();

  // Tech/SaaS detection
  if (content.match(/\b(developer|engineer|code|programming|software|devops|api|frontend|backend|fullstack|tech lead|cto)\b/)) {
    niche = 'developer';
    niche_name = 'Developer/Engineer';
  } 
  // Founder/Startup detection
  else if (content.match(/\b(founder|ceo|co-founder|startup|entrepreneur|building|launched)\b/)) {
    niche = 'founder';
    niche_name = 'Founder/CEO';
  }
  // Product/Design detection
  else if (content.match(/\b(product|pm|product manager|ux|ui|design|figma|designer)\b/)) {
    niche = 'product';
    niche_name = 'Product/Design';
  }
  // Marketing/Growth detection
  else if (content.match(/\b(marketing|growth|seo|revops|demand gen|content marketing|social media marketing)\b/)) {
    niche = 'marketing';
    niche_name = 'Marketing/Growth';
  }
  // Tech Leadership detection
  else if (content.match(/\b(cto|vp eng|tech lead|architect|engineering manager|head of eng)\b/)) {
    niche = 'tech_leadership';
    niche_name = 'Tech Leadership';
  }
  // AI/Data detection
  else if (content.match(/\b(ai|ml|machine learning|data scien|artificial intelligence|llm|gpt|neural)\b/)) {
    niche = 'ai_data';
    niche_name = 'AI/Data Science';
  }
  // Business/Network Marketing detection (NEW!)
  else if (content.match(/\b(network|mlm|biznis|business|mentor|coach|entrepreneur|hustle|side hustle|passive income)\b/)) {
    niche = 'business';
    niche_name = 'Business/Entrepreneurship';
  }
  // Lifestyle/General Creator (fallback)
  else if (content.match(/\b(lifestyle|vlog|influencer|content creator|creator)\b/)) {
    niche = 'lifestyle';
    niche_name = 'Lifestyle Creator';
  }

  // Build classification object
  results.push({
    json: {
      ...item.json,
      classification: {
        tier: tier,
        tier_name: tier_name,
        niche: niche,
        niche_name: niche_name,
        followers: followers,
        engagement_rate: engagement_rate,
        username: username,
        bio: bio,
        primary_platform: platform
      }
    }
  });
}

return results;
