// EXTRACT INPUT FROM FORM - 9 LANGUAGES + WORK TYPE
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const input = $input.first();
const formData = input.json || {};

const regionField = formData['ğŸŒ Region & Language'] || formData['Region & Language'] || '';
const workTypeField = formData['ğŸ—ï¸ Work Type'] || formData['Work Type'] || 'Auto-detect';

// 9 languages mapping
const langMap = {
  'German': 'DE',
  'English': 'EN',
  'Russian': 'RU',
  'Spanish': 'ES',
  'French': 'FR',
  'Portuguese': 'PT',
  'Chinese': 'ZH',
  'Arabic': 'AR',
  'Hindi': 'HI'
};

let languageCode = 'EN';
for (const [lang, code] of Object.entries(langMap)) {
  if (regionField.includes(lang)) {
    languageCode = code;
    break;
  }
}

// Work type detection
let workType = 'auto';
if (workTypeField.includes('New')) workType = 'new_construction';
else if (workTypeField.includes('Renovation')) workType = 'renovation';
else if (workTypeField.includes('Repair')) workType = 'repair';

const userDescription = formData['ğŸ“ Description (optional)'] || formData['Description (optional)'] || '';

let photoBase64 = '';
let photoMimeType = 'image/jpeg';

if (input.binary) {
  for (const key of Object.keys(input.binary)) {
    const bin = input.binary[key];
    if (bin.mimeType && bin.mimeType.startsWith('image/')) {
      photoBase64 = bin.data;
      photoMimeType = bin.mimeType;
      break;
    }
  }
}

return {
  json: {
    language_code: languageCode,
    photo_base64: photoBase64,
    photo_mime_type: photoMimeType,
    has_photo: photoBase64.length > 100,
    user_description: userDescription,
    selected_region: regionField,
    work_type: workType
  }
};

return {
  json: {
    success: false,
    error: true,
    message: 'âŒ No photo provided',
    html_content: '<!DOCTYPE html><html><head><meta charset="UTF-8"></head><body style="font-family:system-ui;padding:40px;text-align:center;"><h1>âŒ Error</h1><p>No photo was uploaded. Please go back and upload a construction photo.</p></body></html>'
  }
};

