// PARSE STAGE 1 VISION RESPONSE
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const aiResponse = $input.first().json;
const configData = $('Configure Language').first().json;

let parsed = {
  room_type: 'unknown',
  room_description: 'Analysis failed',
  elements: [],
  fixtures: [],
  estimated_dimensions: { floor_area_m2: 10, wall_area_m2: 30, ceiling_height_m: 2.5, perimeter_m: 12 }
};

try {
  const content = aiResponse.text || aiResponse.content || aiResponse.response || '';
  let jsonStr = content;

  const jsonMatch = content.match(/```json\s*([\s\S]*?)\s*```/);
  if (jsonMatch) {
    jsonStr = jsonMatch[1];
  } else {
    const codeMatch = content.match(/```\s*([\s\S]*?)\s*```/);
    if (codeMatch) {
      jsonStr = codeMatch[1];
    } else {
      const objMatch = content.match(/\{[\s\S]*\}/);
      if (objMatch) {
        jsonStr = objMatch[0];
      }
    }
  }

  parsed = JSON.parse(jsonStr);
} catch (error) {
  console.error('Parse error:', error.message);
}

return {
  json: {
    ...configData,
    stage1_result: parsed,
    room_type: parsed.room_type || 'unknown',
    room_description: parsed.room_description || 'Photo analysis',
    elements: parsed.elements || [],
    fixtures: parsed.fixtures || [],
    dimensions: parsed.estimated_dimensions || {},
    work_type_detected: parsed.work_type_detected || configData.work_type || 'renovation',
    confidence: parsed.confidence || 'medium'
  }
};

