// PARSE STAGE 4 DECOMPOSITION RESPONSE
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const aiResponse = $input.first().json;
const configData = $('Parse STAGE 1').first().json;

let parsed = { work_items: [] };

try {
  const content = aiResponse.text || aiResponse.content || aiResponse.response || '';
  let jsonStr = content;

  const jsonMatch = content.match(/```json\s*([\s\S]*?)\s*```/);
  if (jsonMatch) {
    jsonStr = jsonMatch[1];
  } else {
    const objMatch = content.match(/\{[\s\S]*\}/);
    if (objMatch) {
      jsonStr = objMatch[0];
    }
  }

  parsed = JSON.parse(jsonStr);
} catch (error) {
  console.error('STAGE 4 Parse error:', error.message);
}

// Validate and enrich work items
const workItems = (parsed.work_items || []).map((work, idx) => {
  // Ensure search_query exists and is meaningful
  let searchQuery = work.search_query || work.work_name || '';

  // Add language-specific terms if needed
  if (searchQuery.length < 5) {
    searchQuery = work.work_name + ' ' + (work.source_element || '');
  }

  return {
    work_id: `W${String(idx + 1).padStart(3, '0')}`,
    work_sequence: work.work_sequence || idx + 1,
    work_category: work.work_category || 'MAIN',
    work_name: work.work_name || 'Unnamed work',
    search_query: searchQuery.trim(),
    project_quantity: parseFloat(work.quantity) || 1,
    unit: work.unit || 'mÂ²',
    calculation_basis: work.calculation_basis || '',
    source_element: work.source_element || '',
    is_demolition: work.is_demolition || false
  };
});

// Sort by sequence
workItems.sort((a, b) => a.work_sequence - b.work_sequence);

return {
  json: {
    ...configData,
    work_items: workItems,
    works_count: workItems.length,
    phases: parsed.phases || ['PREPARATION', 'MAIN', 'FINISHING', 'MEP'],
    stage4_success: workItems.length >= 3
  }
};

