    'm3': ['mÂ³', 'm3', 'cbm', 'ĞºÑƒĞ±.Ğ¼', 'ĞºÑƒĞ±. Ğ¼', 'cu.m', 'ç«‹æ–¹ç±³'],
    'm': ['m', 'Ğ¼', 'lm', 'lfm', 'Ğ¿.Ğ¼', 'Ğ¿. Ğ¼', 'lin.m', 'ç±³'],
    'stk': ['stk', 'stÃ¼ck', 'st', 'ÑˆÑ‚', 'ÑˆÑ‚.', 'pcs', 'ea', 'each', 'unit', 'ä¸ª', 'ä»¶'],
    '100m2': ['100 mÂ²', '100mÂ²', '100 m2', '100m2', '100 qm', '100 ĞºĞ².Ğ¼']
  };
  for (const [normalized, variants] of Object.entries(unitGroups)) {
