// FIX: CORRECT EXTRACTION OF document.pageContent
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
const parsedResults = searchResults.map((item) => {
  const data = item.json || {};

  // FIX: Handle nested document structure from Vector Search
  const doc = data.document || {};
  const content = String(doc.pageContent || doc.content || data.pageContent || data.content || '');
  const metadata = doc.metadata || data.metadata || {};
  const score = data.score || 0;

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // EXTRACT TOTAL COST from "Total cost: 3127.16 EUR"
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  let totalCost = 0;
  const costPatterns = [
    /Total\s*cost:\s*([\d.,]+)\s*(?:EUR|USD|RUB|CAD|CNY|AED|INR|BRL)/i,
    /Resources?\s*cost:\s*([\d.,]+)/i,
    /(?:Gesamt|Ğ˜Ğ¢ĞĞ“Ğ|Ğ’ÑĞµĞ³Ğ¾|Ğ¡Ñ‚Ğ¾Ğ¸Ğ¼Ğ¾ÑÑ‚ÑŒ)[^\d]*([\d.,]+)/i,
    /(?:EUR|USD|RUB|CAD|\$|â‚¬|â‚½)\s*([\d.,]+)/i
  ];
  for (const pattern of costPatterns) {
    const match = content.match(pattern);
    if (match) {
      totalCost = parseFloat(match[1].replace(/,/g, '.').replace(/\s/g, '')) || 0;
      if (totalCost > 0) break;
    }
  }

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // EXTRACT CODE, NAME, UNIT from content if not in metadata
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  let rateCode = metadata.rsts || metadata.code || '';
  let rateName = metadata.names || metadata.name || '';
  let rateUnit = metadata.unit || '';

  // Fallback: extract from content
  if (!rateCode) {
    const codeMatch = content.match(/CODE:\s*([A-ZĞ-Ğ¯a-zĞ°-Ñ0-9_-]+)/i);
    if (codeMatch) rateCode = codeMatch[1];
  }
  if (!rateName) {
    const nameMatch = content.match(/NAME:\s*(.+?)(?:\n|UNIT:|CATEGORY:)/i);
    if (nameMatch) rateName = nameMatch[1].trim();
  }
  if (!rateUnit) {
    const unitMatch = content.match(/UNIT:\s*(.+?)(?:\n|CATEGORY:)/i);
    if (unitMatch) rateUnit = unitMatch[1].trim();
  }

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // EXTRACT LABOR HOURS
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  let laborHours = 0;
  const laborMatch = content.match(/(?:Ñ€Ğ°Ğ·Ñ€ÑĞ´|worker|arbeiter|labor)[^â€”\n]*â€”\s*([\d.,]+)\s*(?:Ñ‡|Ñ‡Ğ°Ñ|std|hour|h)/i);
  if (laborMatch) {
    laborHours = parseFloat(laborMatch[1].replace(',', '.')) || 0;
  }

  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  // EXTRACT RESOURCES from RESOURCES: section
  // â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
  const resources = [];
  const resourcesSection = content.match(/RESOURCES:\s*([\s\S]*?)(?:$|MACHINES:|SCOPE|CLASSIFICATION:)/i);
  if (resourcesSection) {
    const resourcesText = resourcesSection[1];
    const resourceLines = resourcesText.split('\n').filter(line => line.trim().startsWith('-'));

    for (const line of resourceLines) {
      const resMatch = line.match(/^-\s*([A-ZĞ-Ğ¯a-zĞ°-Ñ0-9_-]+)\s*[â€”â€“-]\s*(.+?)\s*[â€”â€“-]\s*([\d.,]+)\s*(.+?)$/i);
      if (resMatch) {
        const resCode = resMatch[1].trim();
        const resName = resMatch[2].trim();
        const resQty = parseFloat(resMatch[3].replace(',', '.')) || 0;
        const resUnit = resMatch[4].trim();
        const resType = detectResourceType(resUnit, resName, resCode);

        resources.push({
          resource_code: resCode,
          resource_name: resName,
          resource_quantity: resQty,
          resource_unit: resUnit,
          resource_cost: 0,
          resource_type: resType
        });
      }
    }
  }

  return {
    rate_code: rateCode,
    rate_name: rateName,
    rate_unit: rateUnit,
    total_cost_position: totalCost,
    worker_labor_hours: laborHours,
    hierarchy: metadata.hierarchy || '',
    resources: resources,
    resources_count: resources.length,
    vector_score: score,
    content_preview: content.substring(0, 300)
  };
});

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// QUALITY SCORING v2.0
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
const expectedUnit = normalizeUnit(workData.expected_unit || workData.unit || '');
const searchQuery = (workData.search_query || workData.work_name || '').toLowerCase();
const workName = (workData.work_name || '').toLowerCase();

const scoredResults = parsedResults.map(result => {
  let score = 0;
  const factors = [];
  const resultUnit = normalizeUnit(result.rate_unit);
  const rateName = (result.rate_name || '').toLowerCase();
  const rateContent = (result.content_preview || '').toLowerCase();
  const combined = rateName + ' ' + rateContent;

  if (result.total_cost_position > 0) { 
    score += 30; 
    factors.push('has_price:' + result.total_cost_position.toFixed(0)); 
  }

  if (result.resources_count > 0) { 
    score += Math.min(25, result.resources_count * 5); 
    factors.push('resources:' + result.resources_count); 
  }

  if (resultUnit && expectedUnit) {
    if (resultUnit === expectedUnit) { 
      score += 20; 
      factors.push('unit_exact'); 
    } else if (resultUnit.includes(expectedUnit) || expectedUnit.includes(resultUnit)) { 
      score += 10; 
      factors.push('unit_partial'); 
    }
  }

  const materialMatches = MATERIAL_KEYWORDS.filter(kw => 
    (workName.includes(kw) || searchQuery.includes(kw)) && combined.includes(kw)
  );
  if (materialMatches.length > 0) {
    score += 15;
    factors.push('material:' + materialMatches[0]);
  }

  const workTypeMatches = WORK_TYPE_KEYWORDS.filter(kw => 
    (workName.includes(kw) || searchQuery.includes(kw)) && combined.includes(kw)
  );
  if (workTypeMatches.length > 0) {
    score += 10;
    factors.push('work_type:' + workTypeMatches[0]);
  }

  const queryWords = searchQuery.split(/\s+/).filter(w => w.length > 3);
  const matchedWords = queryWords.filter(w => rateName.includes(w) || rateContent.includes(w));
  if (matchedWords.length > 0) { 
    score += Math.min(15, matchedWords.length * 5); 
    factors.push('words:' + matchedWords.length); 
  }

  if (result.vector_score > 0.5) {
    score += 10;
    factors.push('vscore:' + result.vector_score.toFixed(2));
  } else if (result.vector_score > 0.4) {
    score += 5;
    factors.push('vscore:' + result.vector_score.toFixed(2));
  }

  const hasLabor = result.resources.some(r => r.resource_type === 'labor');
  const hasMaterial = result.resources.some(r => r.resource_type === 'material');
  if (hasLabor && hasMaterial) {
    score += 5;
    factors.push('complete_rate');
  }

  return { ...result, quality_score: score, quality_factors: factors };
});

const sorted = scoredResults.sort((a, b) => b.quality_score - a.quality_score);
const best = sorted[0] || { quality_score: 0, quality_factors: [], resources: [] };

let qualityLevel = 'not_found';
const s = best.quality_score;
if (s >= 60) { qualityLevel = 'high'; }
else if (s >= 40) { qualityLevel = 'medium'; }
else if (s >= 20) { qualityLevel = 'low'; }

