const projectQuantity = workData.project_quantity || 0;
const unitCost = best.total_cost_position || 0;

let unitDivisor = 1;
const rateUnit = (best.rate_unit || '').toLowerCase();
if (rateUnit.includes('100')) unitDivisor = 100;
else if (rateUnit.includes('10 ')) unitDivisor = 10;

const quantityInRateUnits = projectQuantity / unitDivisor;
const totalCost = quantityInRateUnits * unitCost;
const estimatedLaborHours = (best.worker_labor_hours || 0) * quantityInRateUnits;

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// SCALE & CATEGORIZE RESOURCES
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
let laborCost = 0, machineCost = 0, materialCost = 0;
const scaledResources = (best.resources || []).map(r => {
  const scaledQty = (r.resource_quantity || 0) * quantityInRateUnits;
  const scaledCost = (r.resource_cost || 0) * quantityInRateUnits;

  if (r.resource_type === 'labor') laborCost += scaledCost;
  else if (r.resource_type === 'machine') machineCost += scaledCost;
  else materialCost += scaledCost;

  return { ...r, scaled_quantity: scaledQty, scaled_cost: scaledCost };
});

if (laborCost === 0 && materialCost === 0 && totalCost > 0) {
  laborCost = totalCost * 0.35;
  materialCost = totalCost * 0.55;
  machineCost = totalCost * 0.10;
}

return [{
