.link:hover { text-decoration: underline; }
.toggle-icon { display: inline-block; width: 16px; transition: transform 0.2s; cursor: pointer; }
tr.hidden { display: none; }
.calc-cell { font-size: 9px; color: var(--text-muted); line-height: 1.4; max-width: 200px; }
