.footer-links a { color: var(--primary); text-decoration: none; white-space: nowrap; }
.footer-db { font-size: 8px; color: var(--text-muted); margin-top: 6px; padding-top: 6px; border-top: 1px solid var(--border); }
@media print { body { padding: 0; background: white; } .container { box-shadow: none; } }
</style>
</head>
<body>
<div class="container">
`;

// HEADER
html += '<div class="header-new"><div class="header-left"><div class="header-title-new">' + t.doc_title + ' v2</div><div class="header-project-new">' + escapeHtml(projectName) + '</div><div class="header-info-new">' + t.pricing_level + ': ' + pricingLevel + ' | ' + dateTimeStr + '</div></div><div class="header-right"><a href="https://github.com/datadrivenconstruction" target="_blank" class="header-btn"><svg viewBox="0 0 24 24" fill="currentColor"><path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.+123456789066 1.983-.399 3.+123456789038 3.+12345678907-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/></svg>GitHub</a><a href="https://datadrivenconstruction.io/" target="_blank"><img src="https://datadrivenconstruction.io/wp-content/uploads/2023/07/DataDrivenConstruction-1-1.png" alt="DDC" class="header-logo"></a></div></div>\n';

// PHOTO INFO
html += '<div class="photo-info"><div class="photo-info-title">ğŸ“· ' + t.photo_analysis + '</div><span class="photo-info-item"><strong>' + t.location + ':</strong> ' + escapeHtml(photoAnalysis.room_type || 'N/A') + '</span><span class="photo-info-item"><strong>' + t.room_size + ':</strong> ' + escapeHtml(photoAnalysis.room_size || 'N/A') + '</span><span class="photo-info-item"><strong>' + t.work_phase + ':</strong> ' + escapeHtml(photoAnalysis.work_phase || 'N/A') + '</span><span class="photo-info-item"><strong>Elements:</strong> ' + (photoAnalysis.elements_count || 0) + '</span><span class="photo-info-item"><strong>Fixtures:</strong> ' + (photoAnalysis.fixtures_count || 0) + '</span>';
if (photoAnalysis.materials && photoAnalysis.materials.length > 0) {
  html += '<span class="photo-info-item"><strong>' + t.materials + ':</strong> ' + escapeHtml(photoAnalysis.materials.slice(0, 5).join(', ')) + '</span>';
}
html += '</div>\n';

// VALIDATION BAR
if (validation.passed) {
  html += '<div class="validation-bar validation-passed"><span class="validation-icon">âœ…</span><span class="validation-text">' + t.validation_passed + '</span></div>\n';
} else if (validation.issues && validation.issues.length > 0) {
  html += '<div class="validation-bar validation-issues"><span class="validation-icon">âš ï¸</span><span class="validation-text">' + t.validation_issues + ':</span>';
  validation.issues.forEach(function(issue) {
    html += '<span class="validation-issue">' + escapeHtml(issue) + '</span>';
  });
  html += '</div>\n';
}

// CHARTS SECTION
html += '<div class="charts-section"><div class="row-1"><div class="kpi-row"><div class="kpi-card"><div class="kpi-icon">ğŸ’°</div><div class="kpi-content"><div class="kpi-value">' + formatCurrency(grandTotal) + '</div><div class="kpi-label">' + t.kpi_total + '</div></div></div><div class="kpi-card"><div class="kpi-icon">â±ï¸</div><div class="kpi-content"><div class="kpi-value">' + formatNumber(grandLaborHours, 0) + ' h</div><div class="kpi-label">' + t.kpi_hours + '</div></div></div><div class="kpi-card"><div class="kpi-icon">ğŸ“…</div><div class="kpi-content"><div class="kpi-value">' + laborDays + '</div><div class="kpi-label">' + t.kpi_days + '</div></div></div></div><div class="chart-card"><div class="chart-title">' + t.chart_cost_structure + '</div><div class="h-bar" style="margin-bottom:10px;"><div class="h-bar-track" style="height:8px;"><div class="h-bar-fill" style="width:' + laborPercent + '%;background:#1D1D1F;"></div><div class="h-bar-fill" style="width:' + materialPercent + '%;background:#86868B;"></div><div class="h-bar-fill" style="width:' + machinesPercent + '%;background:#AEAEB2;"></div></div></div><div style="display:flex;flex-direction:column;gap:5px;font-size:10px;"><div style="display:flex;justify-content:space-between;"><span><span style="display:inline-block;width:8px;height:8px;background:#1D1D1F;border-radius:2px;margin-right:6px;"></span>' + t.chart_labor + '</span><span style="font-weight:600;">' + formatCurrency(grandResourceCost) + ' (' + laborPercent + '%)</span></div><div style="display:flex;justify-content:space-between;"><span><span style="display:inline-block;width:8px;height:8px;background:#86868B;border-radius:2px;margin-right:6px;"></span>' + t.chart_material + '</span><span style="font-weight:600;">' + formatCurrency(grandMaterialCost) + ' (' + materialPercent + '%)</span></div><div style="display:flex;justify-content:space-between;"><span><span style="display:inline-block;width:8px;height:8px;background:#AEAEB2;border-radius:2px;margin-right:6px;"></span>' + t.chart_machines + '</span><span style="font-weight:600;">' + formatCurrency(grandMachineCost) + ' (' + machinesPercent + '%)</span></div></div></div></div>';

// Phase chart
var phaseHtml = '';
for (var i = 0; i < phaseChartData.length; i++) {
  var p = phaseChartData[i];
  var pct = grandTotal > 0 ? Math.round(p.cost / grandTotal * 100) : 0;
  phaseHtml += '<div class="h-bar"><div class="h-bar-label"><span>' + escapeHtml(p.name) + '</span><span>' + formatCurrency(p.cost) + '</span></div><div class="h-bar-track"><div class="h-bar-fill" style="width:' + pct + '%;background:#1D1D1F;"></div></div></div>';
}

// Timeline chart
var timelineHtml = '';
for (var i = 0; i < phaseChartData.length; i++) {
  var p = phaseChartData[i];
  var phaseDays = Math.ceil(p.hours / 8);
  var widthPct = laborDays > 0 ? Math.min(phaseDays / laborDays * 100, 100) : 100;
  timelineHtml += '<div class="timeline-row"><div class="timeline-label">' + escapeHtml(p.name) + '</div><div class="timeline-track"><div class="timeline-bar" style="left:0;width:' + widthPct + '%;">' + phaseDays + 'd</div></div></div>';
}

html += '<div class="row-2"><div class="chart-card"><div class="chart-title">' + t.chart_by_phase + '</div>' + phaseHtml + '</div><div class="chart-card"><div class="chart-title">' + t.chart_timeline + '</div>' + timelineHtml + '</div></div>';

// Treemap
var treemapHtml = '';
for (var i = 0; i < phaseChartData.length; i++) {
  var p = phaseChartData[i];
  var pct = grandTotal > 0 ? Math.round(p.cost / grandTotal * 100) : 0;
  treemapHtml += '<div class="treemap-item" data-phase="' + (i+1) + '" style="flex:' + (pct || 1) + ';"><div class="treemap-name">' + escapeHtml(p.name) + '</div><div class="treemap-value">' + formatCurrency(p.cost) + '</div><div class="treemap-percent">' + pct + '%</div></div>';
}
html += '<div class="row-3"><div class="chart-card"><div class="chart-title">' + t.chart_hierarchy + '</div><div class="treemap">' + treemapHtml + '</div></div></div></div>\n';

// TABLE CONTROLS
html += '<div class="table-controls"><button class="control-btn" onclick="collapseAll()">' + t.collapse_all + '</button><button class="control-btn" onclick="expandAll()">' + t.expand_all + '</button></div>\n';

// QUALITY BAR
html += '<div class="quality-bar"><div class="quality-item"><span>' + t.found_rates + ':</span><span class="quality-percent">' + foundPercent + '%</span><span>(' + foundRates + '/' + totalWorks + ')</span></div><div class="quality-item"><span class="quality-dot dot-high"></span><span>' + qualityHigh + '</span></div><div class="quality-item"><span class="quality-dot dot-medium"></span><span>' + qualityMedium + '</span></div><div class="quality-item"><span class="quality-dot dot-low"></span><span>' + qualityLow + '</span></div><div class="quality-item"><span class="quality-dot dot-notfound"></span><span>' + qualityNotFound + '</span></div>';
if (qualityNotFound > 0) {
  html += '<div class="quality-item" style="color:var(--warning)">âš  ' + qualityNotFound + ' ' + t.manual_check + '</div>';
}
html += '</div>\n';

// TABLE
html += '<table><tr class="col-header"><td style="width:4%">' + t.col_pos + '</td><td style="width:9%">' + t.col_code + '</td><td style="width:24%">' + t.col_description + '</td><td style="width:16%">' + t.col_calc + '</td><td style="width:6%">' + t.col_unit + '</td><td style="width:7%" class="right">' + t.col_qty + '</td><td style="width:9%" class="right">' + t.col_price + '</td><td style="width:9%" class="right">' + t.col_total + '</td><td style="width:5%" class="right">' + t.col_labor + '</td><td style="width:4%" class="center">' + t.col_quality + '</td></tr>';

// DATA ROWS
var globalWorkIndex = 0;
for (var phaseIdx = 0; phaseIdx < phases.length; phaseIdx++) {
  var phase = phases[phaseIdx];
  var phaseNum = phaseIdx + 1;
  var phasePercent = grandTotal > 0 ? ((phase.phase_total_cost || 0) / grandTotal * 100).toFixed(0) : '0';

  html += '<tr class="phase" id="phase-' + phaseNum + '" data-phase="' + phaseNum + '"><td>' + phaseNum + '</td><td></td><td colspan="2"><span class="toggle-icon">â–¼</span> ' + escapeHtml(phase.phase_name || 'Phase') + '</td><td></td><td></td><td></td><td class="right num">' + formatCurrency(phase.phase_total_cost || 0) + '</td><td class="right num">' + formatNumber(phase.phase_labor_hours || 0, 1) + '</td><td class="center">' + phasePercent + '%</td></tr>';

  var types = phase.types || [];
  for (var typeIdx = 0; typeIdx < types.length; typeIdx++) {
    var type = types[typeIdx];
    var typeNum = typeIdx + 1;
    var typeName = type.type_name || 'Unknown Type';
    var elementCount = type.element_count || 1;
    var category = type.category || '';

    html += '<tr class="type" data-phase="' + phaseNum + '" data-type="' + typeNum + '"><td>' + phaseNum + '.' + typeNum + '</td><td></td><td><span class="toggle-icon">â–½</span> ' + escapeHtml(typeName) + ' <span style="color:var(--text-muted)">(Ã—' + elementCount + ')</span></td><td class="calc-cell"><span class="calc-type">' + escapeHtml(category) + '</span></td><td></td><td></td><td></td><td class="right num">' + formatCurrency(type.type_total_cost || 0) + '</td><td class="right num">' + formatNumber(type.type_total_labor_hours || 0, 1) + '</td><td></td></tr>';

    var works = type.works || [];
    for (var workIdx = 0; workIdx < works.length; workIdx++) {
      var work = works[workIdx];
      var workNum = workIdx + 1;
      globalWorkIndex++;
      var level = work.quality_level || 'not_found';
      var dotClass = 'dot-notfound';
      if (level === 'high') dotClass = 'dot-high';
      else if (level === 'medium') dotClass = 'dot-medium';
      else if (level === 'low') dotClass = 'dot-low';

      var rateCode = work.rate_code || '';
      var codeLink = (rateCode && rateCode !== 'NOT_FOUND') ? '<a href="' + RATES_LINK + '" class="link" target="_blank">' + escapeHtml(rateCode) + '</a>' : 'â€”';

      var calcDetails = work.calculation_details || {};
      var calcDisplay = '<span class="calc-type">' + escapeHtml(work.calculation_basis || work.source_element || '') + '</span>';
      if (calcDetails.formula_display) {
        calcDisplay += '<span class="calc-formula">' + escapeHtml(calcDetails.formula_display) + '</span>';
      }

      var workRowClass = 'work';
      if (work.is_demolition) workRowClass += ' work-demolition';

      html += '<tr class="' + workRowClass + '" data-phase="' + phaseNum + '" data-type="' + typeNum + '" data-work="' + globalWorkIndex + '"><td style="padding-left:16px">' + phaseNum + '.' + typeNum + '.' + workNum + '</td><td>' + codeLink + '</td><td style="padding-left:24px"><span class="toggle-icon">â–¿</span> ' + (work.is_demolition ? 'ğŸ”¨ ' : '') + escapeHtml(work.rate_name || work.work_name || 'Work') + '</td><td class="calc-cell">' + calcDisplay + '</td><td>' + escapeHtml(work.rate_unit || work.project_unit || 'â€”') + '</td><td class="right num">' + formatNumber(work.calculated_quantity || work.quantity_in_rate_units || work.project_quantity || 0, 4) + '</td><td class="right num">' + formatCurrency(work.unit_cost || 0) + '</td><td class="right num">' + formatCurrency(work.total_cost || 0) + '</td><td class="right num">' + formatNumber(work.estimated_labor_hours || 0, 1) + '</td><td class="center"><span class="quality-dot ' + dotClass + '"></span></td></tr>';

      var resources = work.resources_all || [];
      for (var resIdx = 0; resIdx < resources.length; resIdx++) {
        var res = resources[resIdx];
        var isLast = resIdx === resources.length - 1;
        var prefix = isLast ? 'â””' : 'â”œ';
        var resType = res.resource_type || 'material';
        var tagLabels = { labor: t.res_labor, material: t.res_material, machine: t.res_machine };
        var tagLabel = tagLabels[resType] || t.res_material;
        var tagClass = 'res-tag res-tag-' + resType;

        html += '<tr class="resource" data-phase="' + phaseNum + '" data-type="' + typeNum + '" data-work="' + globalWorkIndex + '"><td></td><td style="font-size:9px;"><span style="opacity:0.5;">' + escapeHtml(res.resource_code || '') + '</span><span class="' + tagClass + '">' + tagLabel + '</span></td><td style="padding-left:36px">' + prefix + ' ' + escapeHtml(res.resource_name || 'Resource') + '</td><td></td><td>' + escapeHtml(res.resource_unit || '') + '</td><td class="right num">' + formatNumber(res.scaled_quantity || res.resource_quantity || 0, 4) + '</td><td class="right num">' + formatCurrency(res.price_per_unit || 0) + '</td><td class="right num">' + formatCurrency(res.scaled_cost || res.resource_cost || 0) + '</td><td></td><td></td></tr>';
      }
    }
  }

  html += '<tr class="subtotal" data-phase="' + phaseNum + '"><td></td><td></td><td colspan="2">' + t.subtotal + ' ' + phaseNum + '</td><td></td><td></td><td></td><td class="right num">' + formatCurrency(phase.phase_total_cost || 0) + '</td><td class="right num">' + formatNumber(phase.phase_labor_hours || 0, 1) + '</td><td class="center">' + phasePercent + '%</td></tr>';
}

// GRAND TOTAL
html += '<tr class="grand-total"><td colspan="4" style="padding-left:16px">' + t.grand_total + '</td><td></td><td></td><td></td><td class="right num">' + formatCurrency(grandTotal) + '</td><td class="right num">' + formatNumber(grandLaborHours, 1) + '</td><td class="center">100%</td></tr>';
html += '<tr class="grand-total-info"><td colspan="4" style="padding-left:16px">' + t.labor_cost + ': <span class="highlight">' + formatCurrency(grandResourceCost) + '</span> (' + laborPercent + '%)</td><td colspan="3">' + t.material_cost + ': <span class="highlight">' + formatCurrency(grandMaterialCost) + '</span></td><td colspan="3" class="right" style="padding-right:16px"><span class="highlight">' + laborDays + '</span> ' + t.labor_days + '</td></tr>';
html += '</table>\n';

// FOOTER
html += '<div class="footer"><div class="footer-main"><div class="footer-brand">OPEN CONSTRUCTION ESTIMATE v2</div><div class="footer-info">Professional cost estimation powered by DDC CWICR database | Multi-stage AI decomposition</div><div class="footer-links"><a href="' + RATES_LINK + '" target="_blank">openconstructionestimate.com</a><a href="https://github.com/datadrivenconstruction/OpenConstructionEstimate-DDC-CWICR" target="_blank">GitHub</a><a href="https://datadrivenconstruction.io" target="_blank">datadrivenconstruction.io</a></div></div><div class="footer-db">DDC CWICR â€” Construction Work Items, Costs & Resources Database | Generated: ' + dateTimeStr + '</div></div>\n';

html += '</div>\n';

// SCRIPT
html += `<script>
function collapseAll() {
  document.querySelectorAll("tr.phase").forEach(function(row) {
    row.classList.add("collapsed");
    var icon = row.querySelector(".toggle-icon");
    if (icon) icon.textContent = "â–¶";
    var phaseId = row.dataset.phase;
    document.querySelectorAll('tr[data-phase="' + phaseId + '"]:not(.phase)').forEach(function(child) {
      child.classList.add("hidden");
    });
  });
}

function expandAll() {
  document.querySelectorAll("tr.phase").forEach(function(row) {
    row.classList.remove("collapsed");
