document.addEventListener("DOMContentLoaded", function() {
  document.querySelectorAll("tr.phase").forEach(function(row) {
    row.addEventListener("click", function(e) {
      if (e.target.tagName === "A") return;
      var phaseId = this.dataset.phase;
      this.classList.toggle("collapsed");
      var icon = this.querySelector(".toggle-icon");
      var isCollapsed = this.classList.contains("collapsed");
      if (icon) icon.textContent = isCollapsed ? "â–¶" : "â–¼";
      document.querySelectorAll('tr[data-phase="' + phaseId + '"]:not(.phase)').forEach(function(child) {
        if (isCollapsed) {
          child.classList.add("hidden");
        } else {
          if (child.classList.contains("resource")) {
            var workId = child.dataset.work;
            var parentWork = document.querySelector('tr.work[data-work="' + workId + '"]');
            if (parentWork && !parentWork.classList.contains("collapsed")) {
              child.classList.remove("hidden");
            }
          } else if (child.classList.contains("work")) {
            var typeId = child.dataset.type;
            var parentType = document.querySelector('tr.type[data-phase="' + phaseId + '"][data-type="' + typeId + '"]');
            if (parentType && !parentType.classList.contains("collapsed")) {
              child.classList.remove("hidden");
            }
          } else {
            child.classList.remove("hidden");
          }
        }
      });
    });
  });

  document.querySelectorAll("tr.type").forEach(function(row) {
    row.addEventListener("click", function(e) {
      e.stopPropagation();
      if (e.target.tagName === "A") return;
      var phaseId = this.dataset.phase;
      var typeId = this.dataset.type;
      this.classList.toggle("collapsed");
      var icon = this.querySelector(".toggle-icon");
      var isCollapsed = this.classList.contains("collapsed");
      if (icon) icon.textContent = isCollapsed ? "â–·" : "â–½";

      document.querySelectorAll('tr.work[data-phase="' + phaseId + '"][data-type="' + typeId + '"]').forEach(function(work) {
        if (isCollapsed) {
          work.classList.add("hidden");
        } else {
          work.classList.remove("hidden");
        }
      });

      document.querySelectorAll('tr.resource[data-phase="' + phaseId + '"][data-type="' + typeId + '"]').forEach(function(res) {
        if (isCollapsed) {
          res.classList.add("hidden");
        } else {
          var workId = res.dataset.work;
          var parentWork = document.querySelector('tr.work[data-work="' + workId + '"]');
          if (parentWork && !parentWork.classList.contains("collapsed")) {
            res.classList.remove("hidden");
          }
        }
      });
    });
  });

  document.querySelectorAll("tr.work").forEach(function(row) {
    row.addEventListener("click", function(e) {
      e.stopPropagation();
      if (e.target.tagName === "A") return;
      var workId = this.dataset.work;
      this.classList.toggle("collapsed");
      var icon = this.querySelector(".toggle-icon");
      var isCollapsed = this.classList.contains("collapsed");
      if (icon) icon.textContent = isCollapsed ? "â–¹" : "â–¿";

      document.querySelectorAll('tr.resource[data-work="' + workId + '"]').forEach(function(res) {
        if (isCollapsed) {
          res.classList.add("hidden");
        } else {
          res.classList.remove("hidden");
        }
      });
    });
  });

  document.querySelectorAll(".treemap-item").forEach(function(item) {
    item.addEventListener("click", function() {
      var phaseId = this.dataset.phase;
      var targetRow = document.getElementById("phase-" + phaseId);
      if (targetRow) {
        if (targetRow.classList.contains("collapsed")) targetRow.click();
        targetRow.scrollIntoView({ behavior: "smooth", block: "start" });
        targetRow.style.background = "#FFFBEB";
        setTimeout(function() { targetRow.style.background = ""; }, 1500);
      }
    });
  });

  // Default: hide resources
  document.querySelectorAll("tr.resource").forEach(function(row) {
    row.classList.add("hidden");
  });
  document.querySelectorAll("tr.work").forEach(function(row) {
    row.classList.add("collapsed");
    var icon = row.querySelector(".toggle-icon");
    if (icon) icon.textContent = "â–¹";
  });
});
</script>
