// ‚îÄ‚îÄ Extract & normalize Eventbrite attendee payload ‚îÄ‚îÄ
const payload = item.json;

// Eventbrite wraps data differently depending on event type
// Handle both direct payload and nested structures
const attendee = payload.attendees?.[0] || payload.attendee || payload;
const profile = attendee.profile || attendee;
const event = payload.event || {};

// Determine trigger event type from payload
let trigger_event = 'registered';
if (attendee.checked_in === true) trigger_event = 'checked_in';
else if (payload.action === 'attendee.updated' || payload.changed) trigger_event = 'updated';

// Extract core identity
const email = profile.email || attendee.email || null;
const first_name = profile.first_name || attendee.first_name || '';
const last_name = profile.last_name || attendee.last_name || '';
const full_name = (first_name + ' ' + last_name).trim() || null;

// Extract order & event context
const order_id = attendee.order_id || payload.order_id || null;
const event_id = attendee.event_id || payload.event_id || null;
const event_name = event.name?.text || event.name || 'the event';
const ticket_class = attendee.ticket_class_name || attendee.ticket_class?.name || 'General';
const registration_date = attendee.created || attendee.changed || new Date().toISOString();
const checked_in = attendee.checked_in || false;
const checked_in_at = attendee.checked_in_at || null;

// Extract custom question answers (if any)
const answers = Array.isArray(attendee.answers) ? attendee.answers : [];
const custom_answers = {};
answers.forEach(a => {
  if (a.question && a.answer) {
    custom_answers[a.question.toLowerCase().replace(/\s+/g, '_')] = a.answer;
  }
});

// Try to extract social handles from answers if attendee self-reported them
const instagram_from_answers = custom_answers.instagram || custom_answers.instagram_handle || null;
const tiktok_from_answers = custom_answers.tiktok || custom_answers.tiktok_handle || null;
const youtube_from_answers = custom_answers.youtube || custom_answers.youtube_channel || null;

if (!email) {
  throw new Error('Attendee has no email address ‚Äî cannot enrich');
}

return {
  json: {
    attendee_context: {
      trigger_event,
      email,
      full_name,
      first_name,
      last_name,
      order_id,
      event_id,
      event_name,
      ticket_class,
      registration_date,
      checked_in,
      checked_in_at,
      custom_answers,
      self_reported_handles: {
        instagram: instagram_from_answers,
        tiktok: tiktok_from_answers,
        youtube: youtube_from_answers
      }
    }
  }
};

// Item 0 = classification
const classification = items[0].json.output || {};

// Item 1 = enrichment / normalized profile
const normalized_profile = items[1].json.result || {};

return [
  {
    json: {
      normalized_profile,
      output: classification
    }
  }
];
