parameters.jsCode:
const items = $input.all();
const results = [];

for (const item of items) {
  const decision = item.json.decision || 'pending';
  const approver = item.json.approver || 'unknown';
  const comments = item.json.comments || '';
  const timestamp = new Date().toISOString();

  results.push({
    json: {
      decision: decision,
      approver: approver,
      comments: comments,
      timestamp: timestamp,
      approvalId: `APR-${Date.now()}`,
      status: decision === 'approved' ? 'APPROVED' : decision === 'rejected' ? 'REJECTED' : 'PENDING'
    }
  });
}

return results;

