const items = $input.all();
const auditEntries = [];

for (const item of items) {
  const auditEntry = {
    json: {
      auditId: `AUDIT-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date().toISOString(),
      eventType: item.json.output?.actionType || item.json.decision || 'UNKNOWN',
      priority: item.json.output?.priority || 'MEDIUM',
      complianceStatus: item.json.output?.complianceStatus || 'COMPLIANT',
      actionsTaken: item.json.output?.workflowActions?.map(a => a.action).join(', ') || item.json.status || 'N/A',
      approvalRequired: item.json.output?.auditTrail?.approvalRequired || false,
      approvalStatus: item.json.status || 'N/A',
      approver: item.json.approver || 'SYSTEM',
      reasoning: item.json.output?.reasoning || item.json.comments || 'Automated action',
      traceabilityId: item.json.output?.auditTrail?.analysisId || `TRACE-${Date.now()}`,
      metadata: {
        workflowId: $workflow.id,
        executionId: $execution.id,
        nodeExecutionId: $execution.id
      }
    }
  };

  auditEntries.push(auditEntry);
}

return auditEntries;
