parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "overallHealth": {
      "type": "string",
      "description": "Overall funnel health: Excellent, Good, Fair, Poor, Critical"
    },
    "conversionRates": {
      "type": "object",
      "properties": {
        "applicationToScreen": {"type": "number"},
        "screenToPhone": {"type": "number"},
        "phoneToOnsite": {"type": "number"},
        "onsiteToOffer": {"type": "number"},
        "offerToAcceptance": {"type": "number"}
      }
    },
    "bottlenecks": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "stage": {"type": "string"},
          "severity": {"type": "string"},
          "impact": {"type": "string"}
        }
      }
    },
    "goalPerformance": {
      "type": "object",
      "properties": {
        "applicationsVsGoal": {"type": "number"},
        "interviewsVsGoal": {"type": "number"},
        "offersVsGoal": {"type": "number"}
      }
    },
    "topRecommendations": {
      "type": "array",
      "items": {"type": "string"}
    },
    "priority": {
      "type": "string",
      "description": "Priority level: High, Medium, Low"
    },
    "reasoning": {
      "type": "string",
      "description": "Detailed reasoning for the analysis"
    }
  },
  "required": ["overallHealth", "conversionRates", "bottlenecks", "goalPerformance", "topRecommendations", "priority", "reasoning"]
}

{
  "type": "object",
  "properties": {
    "orchestrationSummary": {
      "type": "string",
      "description": "Summary of orchestration decisions and agent coordination"
    },
    "agentsCalled": {
      "type": "array",
      "items": {"type": "string"},
      "description": "List of agent tools that were called"
    },
    "sourcingInsights": {
      "type": "object",
      "description": "Insights from Candidate Sourcing Agent"
    },
    "interviewInsights": {
      "type": "object",
      "description": "Insights from Interview Scheduling Agent"
    },
    "assessmentInsights": {
      "type": "object",
      "description": "Insights from Candidate Assessment Agent"
    },
    "consolidatedActions": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "action": {"type": "string"},
          "priority": {"type": "string"},
          "expectedImpact": {"type": "string"},
          "owner": {"type": "string"}
        }
      }
    },
    "overallPriority": {
      "type": "string",
      "description": "Overall priority: High, Medium, Low"
    },
    "criticalAlerts": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Critical issues requiring immediate attention"
    }
  },
  "required": ["orchestrationSummary", "agentsCalled", "consolidatedActions", "overallPriority"]
}

{
  "type": "object",
  "properties": {
    "processImprovements": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "improvement": {"type": "string"},
          "stage": {"type": "string"},
          "expectedImpact": {"type": "string"}
        }
      }
    },
    "schedulingOptimizations": {
      "type": "array",
      "items": {"type": "string"}
    },
    "candidateExperienceEnhancements": {
      "type": "array",
      "items": {"type": "string"}
    },
    "estimatedConversionIncrease": {
      "type": "number",
      "description": "Estimated percentage increase in interview conversion"
    },
    "estimatedTimeReduction": {
      "type": "number",
      "description": "Estimated days reduction in time-to-hire"
    }
  },
  "required": ["processImprovements", "schedulingOptimizations", "estimatedConversionIncrease"]
}

{
  "type": "object",
  "properties": {
    "offerStrategies": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "strategy": {"type": "string"},
          "category": {"type": "string"},
          "expectedImpact": {"type": "string"}
        }
      }
    },
    "qualityImprovements": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "improvement": {"type": "string"},
          "assessmentStage": {"type": "string"}
        }
      }
    },
    "declineReasons": {
      "type": "array",
      "items": {"type": "string"},
      "description": "Identified reasons for offer declines"
    },
    "estimatedAcceptanceIncrease": {
      "type": "number",
      "description": "Estimated percentage increase in offer acceptance"
    },
    "competitivenessScore": {
      "type": "number",
      "description": "Current offer competitiveness score 0-100"
    }
  },
  "required": ["offerStrategies", "qualityImprovements", "estimatedAcceptanceIncrease", "competitivenessScore"]
}

