parameters.jsCode:
// Log audit trail for quality event processing
const timestamp = new Date().toISOString();
const orchestratorOutput = $json.output || {};

const auditLog = {
  timestamp: timestamp,
  eventType: "QUALITY_EVENT_PROCESSED",
  riskLevel: orchestratorOutput.overallRiskLevel || "UNKNOWN",
  actionRequired: orchestratorOutput.actionRequired || "NONE",
  traceabilityValidated: orchestratorOutput.traceabilityValidated || false,
  riskAssessmentComplete: orchestratorOutput.riskAssessmentComplete || false,
  recallOrchestrationComplete: orchestratorOutput.recallOrchestrationComplete || false,
  agentsCalled: orchestratorOutput.agentsCalled || [],
  summary: orchestratorOutput.summary || "No summary available",
  notificationsSent: true,
  workflowStatus: "COMPLETED"
};

console.log("Audit Trail:", JSON.stringify(auditLog, null, 2));

return [{ json: auditLog }];
