const input = $input.first().json;
const url = input.body?.github_url || input.body?.repository_url || input['GitHub Repository URL'];

if (!url) {
  throw new Error('Missing GitHub URL. Send body.github_url or body.repository_url (webhook) or fill the form field.');
}

const match = url.match(/github\.com\/([^\/]+)\/([^\/\.]+)/);

if (!match) {
  throw new Error(`Invalid GitHub URL: ${url}`);
}

return [{
  json: {
    github_url: url,
    owner: match[1],
    repo: match[2]
  }
}];

const treeData = $input.first().json;
const tree = treeData.tree || [];
const owner = $('Parse Owner and Repo').first().json.owner;
const repo = $('Parse Owner and Repo').first().json.repo;

const filteredTree = tree.filter(item => {
  const p = item.path.toLowerCase();
  return !p.includes('node_modules') &&
         !p.includes('.git/') &&
         !p.includes('package-lock.json') &&
         !p.includes('yarn.lock') &&
         !p.includes('dist/') &&
         !p.includes('build/') &&
         !p.includes('__pycache__') &&
         !p.includes('.egg-info');
});

let fileTreeStr = filteredTree
  .filter(item => item.type === 'blob')
  .map(item => item.path)
  .join('\n');
if (fileTreeStr.length > 40000) {
  fileTreeStr = fileTreeStr.substring(0, 40000) + '\n... [TREE TRUNCATED]';
}

const keyPatterns = [
  /^main\.py$/i,
  /^app\.py$/i,
  /^index\.(js|ts|tsx)$/i,
  /^server\.(js|ts)$/i,
  /^package\.json$/i,
  /^requirements\.txt$/i,
  /^pyproject\.toml$/i,
  /^Cargo\.toml$/i,
  /^go\.mod$/i,
  /^setup\.py$/i,
  /^setup\.cfg$/i,
  /^docker-compose\.ya?ml$/i,
  /^Dockerfile$/i,
  /^Makefile$/i,
  /^config\/.*\.(ya?ml|json|toml)$/i,
  /^src\/index\.(js|ts|tsx)$/i,
  /^src\/main\.(py|js|ts|rs)$/i,
  /^src\/app\.(py|js|ts)$/i,
  /^\.github\/workflows\/.*\.ya?ml$/i,
  /^streamlit_app\.py$/i,
  /^train.*\.py$/i,
  /^model.*\.py$/i,
  /^predict.*\.py$/i,
  /^Pipfile$/i,
  /^environment\.ya?ml$/i
];

const keyFiles = filteredTree
  .filter(item => item.type === 'blob')
  .filter(item => keyPatterns.some(pattern => pattern.test(item.path)))
  .map(item => item.path)
  .slice(0, 12);

const totalFiles = filteredTree.filter(i => i.type === 'blob').length;
const totalDirs = filteredTree.filter(i => i.type === 'tree').length;

if (keyFiles.length === 0) {
  return [{
    json: { owner, repo, filePath: null, fileTreeStr, totalFiles, totalDirs }
  }];
}

return keyFiles.map(filePath => ({
  json: { owner, repo, filePath, fileTreeStr, totalFiles, totalDirs }
}));

const items = $input.all();
const owner = $('Parse Owner and Repo').first().json.owner;
const repo = $('Parse Owner and Repo').first().json.repo;
const metadata = $('Fetch Repo Metadata').first().json;
const description = metadata.description || '';
const topics = (metadata.topics || []).join(', ');
const language = metadata.language || '';
const fileTreeStr = $('Identify Key Files').first().json.fileTreeStr;
const totalFiles = $('Identify Key Files').first().json.totalFiles;
const totalDirs = $('Identify Key Files').first().json.totalDirs;

const fileContents = items
  .filter(item => item.json.content && item.json.path)
  .map(item => {
    try {
      const decoded = Buffer.from(item.json.content, 'base64').toString('utf-8');
      const truncated = decoded.length > 3000
        ? decoded.substring(0, 3000) + '\n... [TRUNCATED]'
        : decoded;
      return `--- FILE: ${item.json.path} ---\n${truncated}\n`;
    } catch(e) {
      return `--- FILE: ${item.json.path} --- [BINARY/UNREADABLE]\n`;
    }
  })
  .join('\n');

const prompt = `Repository: ${owner}/${repo}\nDescription: ${description || 'No description provided'}\nTopics: ${topics || 'None'}\nPrimary Language: ${language || 'Unknown'}\nTotal files: ${totalFiles} | Total directories: ${totalDirs}\n\n=== FULL FILE TREE ===\n${fileTreeStr}\n\n=== KEY FILE CONTENTS ===\n${fileContents}`;

return [{
  json: {
    owner,
    repo,
    llmInput: prompt,
    fileTreeStr,
    totalFiles,
    totalDirs
  }
}];

const input = $input.first().json;
let md = input.text || '';

// Fix: Ensure space after # headers
md = md.replace(/^(#{1,6})([^\s#])/gm, '$1 $2');

// Fix: Ensure blank line before headers
md = md.replace(/([^\n])\n(#{1,6}\s)/g, '$1\n\n$2');

// Fix: Ensure blank line before code blocks
md = md.replace(/([^\n])\n```/g, '$1\n\n```');

// Fix: Ensure blank line after code blocks
md = md.replace(/```\n([^\n`])/g, '```\n\n$1');

// Fix: Remove trailing whitespace on lines
md = md.replace(/[ \t]+$/gm, '');

return [{
  json: {
    ...input,
    text: md
  }
}];

const analysisOutput = $input.first().json.text || '';
const owner = $('Parse Owner and Repo').first().json.owner;
const repo = $('Parse Owner and Repo').first().json.repo;
const description = $('Fetch Repo Metadata').first().json.description || '';
const totalFiles = $('Prepare LLM Input').first().json.totalFiles;
const totalDirs = $('Prepare LLM Input').first().json.totalDirs;
const defaultBranch = $('Fetch Repo Metadata').first().json.default_branch || 'main';
const now = new Date().toISOString().split('T')[0];

const mermaidBlocks = [];
const mermaidRegex = /```mermaid\n([\s\S]*?)```/g;
let m;
while ((m = mermaidRegex.exec(analysisOutput)) !== null) {
  mermaidBlocks.push(m[1].trim());
}

const markdown = `# System Blueprint: ${owner}/${repo}\n\n> ${description || 'Architecture analysis'}\n>\n> Auto-generated on ${now} by Repo-to-Blueprint Architect\n\n${analysisOutput.trim()}\n\n---\n\n## Repository Stats\n| Metric | Value |\n|--------|-------|\n| Total Files | ${totalFiles} |\n| Total Directories | ${totalDirs} |\n| Generated | ${now} |\n| Source | [${owner}/${repo}](https://github.com/${owner}/${repo}) |\n\n---\n\n*Generated by Repo-to-Blueprint Architect via n8n*\n`;

const mermaidPreview = mermaidBlocks.length > 0 ? mermaidBlocks[0].substring(0, 1500) : '[No diagram]';

return [{
  json: {
    owner,
    repo,
    markdownContent: markdown,
    mermaidCode: mermaidPreview,
    diagramCount: mermaidBlocks.length,
    commitMessage: `docs: update system blueprint (${now})`,
    defaultBranch
  }
}];

const dashData = $('Generate Dashboard HTML').first().json;
const existing = $input.first().json;

const content = Buffer.from(dashData.markdownContent).toString('base64');

const body = {
  message: dashData.commitMessage,
  content: content,
  branch: dashData.defaultBranch
};

if (existing.sha) {
  body.sha = existing.sha;
}

return [{
  json: {
    owner: dashData.owner,
    repo: dashData.repo,
    pushBodyStr: JSON.stringify(body),
    isUpdate: !!existing.sha,
    diagramCount: dashData.diagramCount,
    mermaidCode: dashData.mermaidCode
  }
}];
