const data = $input.item.json;

// Valid options
const validCategories = [
  "Bug Report", 
  "Feature Request", 
  "Billing Question", 
  "General Inquiry", 
  "Technical Support", 
  "Marketing Email"
];

const validUrgencies = ["Low", "Medium", "High", "Critical"];

// Validation and auto-fix
let validationIssues = [];

// Validate category
if (!data.category || !validCategories.includes(data.category)) {
  validationIssues.push(`Invalid category: ${data.category}`);
  data.category = "General Inquiry";
}

// Validate urgency
if (!data.urgency || !validUrgencies.includes(data.urgency)) {
  validationIssues.push(`Invalid urgency: ${data.urgency}`);
  data.urgency = "Medium";
}

// Validate summary
if (!data.summary || data.summary.length < 10) {
  validationIssues.push("Summary too short or missing");
  data.summary = "AI analysis incomplete - requires manual review";
  data.requires_manual_review = true;
}

// Validate suggested_action
if (!data.suggested_action || data.suggested_action.length < 5) {
  validationIssues.push("Suggested action missing");
  data.suggested_action = "Review ticket and respond accordingly";
}

// Add validation metadata
data.validation_status = validationIssues.length > 0 ? "fixed" : "passed";
data.validation_issues = validationIssues.length > 0 ? validationIssues.join("; ") : "none";

return { json: data };

const ticket = $input.item.json;

// ========================================
// ASSIGNMENT RULES
// ========================================

let assignTo = "";
let assignToEmail = "";
let priority = "";
let tags = [];

// Category-based assignment
if (ticket.category === "Bug Report") {
  assignTo = "Engineering Team";
  assignToEmail = "user@example.com";
  priority = ticket.urgency === "Critical" ? "urgent" : "high";
  tags = ["bug", "technical"];

} else if (ticket.category === "Billing Question") {
  assignTo = "Billing Team";
  assignToEmail = "user@example.com";
  priority = "normal";
  tags = ["billing", "finance"];

} else if (ticket.category === "Feature Request") {
  assignTo = "Product Team";
  assignToEmail = "user@example.com";
  priority = "low";
  tags = ["feature-request", "product"];

} else if (ticket.category === "Technical Support") {
  assignTo = "Support Team";
  assignToEmail = "user@example.com";
  priority = ticket.urgency === "Critical" ? "urgent" : "normal";
  tags = ["support", "technical"];

} else {
  assignTo = "General Support";
  assignToEmail = "user@example.com";
  priority = "normal";
  tags = ["general"];
}

// ========================================
// SLA CALCULATION
// ========================================

const slaHours = {
  "Critical": 1,
  "High": 4,
  "Medium": 24,
  "Low": 48
};

const now = new Date();
const deadline = new Date(now);
deadline.setHours(deadline.getHours() + slaHours[ticket.urgency]);

// ========================================
// ESCALATION FLAGS
// ========================================

let requiresEscalation = false;
let escalationReason = "";

// Check for angry keywords
const text = (ticket.subject + " " + ticket.summary).toLowerCase();
if (text.includes("angry") || text.includes("frustrated") || 
    text.includes("terrible") || text.includes("worst")) {
  requiresEscalation = true;
  escalationReason = "Negative sentiment detected";
}

// Check for churn risk
if (text.includes("cancel") || text.includes("refund") || 
    text.includes("competitor") || text.includes("switching")) {
  requiresEscalation = true;
  escalationReason = escalationReason ? escalationReason + "; Churn risk" : "Churn risk";
  tags.push("churn-risk");
}

// Critical urgency always escalates
if (ticket.urgency === "Critical") {
  requiresEscalation = true;
  escalationReason = escalationReason ? escalationReason + "; Critical urgency" : "Critical urgency";
}

// ========================================
// RETURN ENRICHED DATA
// ========================================

return {
  json: {
    ...ticket,
    assigned_to: assignTo,
    assigned_to_email: assignToEmail,
    clickup_priority: priority,
    clickup_tags: tags,
    sla_deadline: deadline.toISOString(),
    sla_deadline_readable: deadline.toLocaleString('en-US', { 
      dateStyle: 'medium', 
      timeStyle: 'short' 
    }),
    sla_hours: slaHours[ticket.urgency],
    requires_escalation: requiresEscalation,
    escalation_reason: escalationReason || "None"
  }
};

// Get AI response
const aiResponse = $input.first().json.choices[0].message.content;

// Parse JSON
const parsed = JSON.parse(aiResponse);

// Get email data from Edit Fields node
const emailData = $('Assign Email Values').first().json;

// Return combined data
return {
  json: {
    timestamp: new Date().toISOString(),
    from: emailData.from,
    subject: emailData.subject,
    emailId: emailData.emailId,
    category: parsed.category,
    urgency: parsed.urgency,
    summary: parsed.summary,
    suggested_action: parsed.suggested_action
  }
};

